// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.4.22 <0.9.0;

contract Ballot{

    address public chairperson;

    struct Voter{
        uint vote;
        address delegate;
        uint weight;
        bool voted;
    }

    struct Proposal{
        bytes32 name;
        uint voteCount;

    }

    mapping (address => Voter) public voters;

    Proposal[] public proposals;



    // constructor (){
