    //     voters[chairperson].weight = 1;

    //     proposals.push(Proposal({name: "Test", voteCount: 0}));
    // }

    function giveRightToVote(address voter) external {
        require(msg.sender == chairperson, "Only Chairperson can give right");

        require(!voters[voter].voted, "Already Voted");

        require(voters[voter].weight == 0 ,"No Access to Vote");

        voters[voter].weight = 1;
    }


    // function delegate(address to) external {
    //     Voter storage sender = voters[msg.sender];

    //     require(sender.weight != 0, "You have no right to vote.");

    //     require(!sender.voted,"Already voted");

    //     require(to != msg.sender, "Self delagate is not allowed");

    //     while (voters[to].delegate != address(0)) {
    //         to = voters[to].delegate;

    //         require(to != msg.sender, "Found loop in delegation.");
    //     }

    //     Voter storage delegate_ = voters[to];

    //     require(delegate_.weight >= 1);

    //     sender.voted = true;
    //     sender.delegate = to;

    //     if (delegate_.voted) {
    //         proposals[delegate_.vote].voteCount += sender.weight;

    //     } else {
    //         delegate_.weight += sender.weight;
    //     }

    // }


    function vote(uint proposal) external {
        Voter storage sender = voters[msg.sender];

        require(!sender.voted,"Already voted");

        require(sender.weight != 0, "No right to Vote");

        sender.voted = true;

        sender.vote = proposal;

        proposals[proposal].voteCount += sender.weight;
    }


    function winningProposal() public view returns(uint _winningProposal){
        uint winningVoteCount = 0;

        for(uint i = 0; i < proposals.length; i++){
            if(proposals[i].voteCount > winningVoteCount){
                winningVoteCount = proposals[i].voteCount;
                _winningProposal = i;
            }
        }
    }


    function winnerName() public view returns(bytes32 _winnerName){
        _winnerName = proposals[winningProposal()].name;

    }


}
